MYSQL.LANGUAGES:
CREATE TABLE LANGUAGES ( LANGUAGECODE VARCHAR(3) NOT NULL,
             LANGUAGENAME VARCHAR(10) NOT NULL,PRIMARY KEY (LANGUAGECODE)
             )ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='Languages'

MYSQL.CHAINS:
CREATE TABLE CHAINS (CHAINCODE VARCHAR(5) NOT NULL, CHAINNAME
          VARCHAR(50) NOT NULL, PRIMARY KEY(CHAINCODE)) ENGINE=InnoDB DEFAULT
          CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='Chains';

MYSQL.COUNTRIES:
CREATE TABLE COUNTRIES(COUNTRYCODE VARCHAR(3) NOT NULL, PRIMARY
             KEY(COUNTRYCODE)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
             COMMENT='Countries';

MYSQL.BOARD_TYPES:
CREATE TABLE BOARD_TYPES(BOARDTYPE VARCHAR(7) NOT NULL, PRIMARY
               KEY(BOARDTYPE)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
               COMMENT='BoardTypes';

MYSQL.TERMINALS:
CREATE TABLE TERMINALS(TERMINALCODE VARCHAR(5) NOT NULL,
             TERMINALNAME VARCHAR(50) NOT NULL, PRIMARY KEY(TERMINALCODE))
             ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='Terminals';

MYSQL.CHARACTERISTICS:
CREATE TABLE CHARACTERISTICS(CHARACTERISTICCODE VARCHAR(19)
                   NOT NULL, PRIMARY KEY(CHARACTERISTICCODE)) ENGINE=InnoDB
                   DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COLLATE=utf8_general_ci COMMENT='Characteristics';

MYSQL.FACILITY_GROUPS:
CREATE TABLE FACILITY_GROUPS(GROUP_ VARCHAR(3) NOT NULL,
                   PRIMARY KEY(GROUP_)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
                   COMMENT='FacilityGroups';

MYSQL.TRANSFER_ZONE:
CREATE TABLE TRANSFER_ZONE(ZONETRANSFER VARCHAR(19) NOT NULL,
                 PRIMARY KEY(ZONETRANSFER)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
                 COMMENT='TransferZone';

ORACLE.DESTINATIONS:
CREATE TABLE DESTINATIONS(DESTINATIONCODE VARCHAR2(3) NOT NULL,
                COUNTRYCODE VARCHAR2(3), PRIMARY KEY(DESTINATIONCODE)); 
                ALTER TABLE DESTINATIONS ADD CONSTRAINT DESTINATIONS_COUNTRIES_FK
                FOREIGN KEY (COUNTRYCODE) REFERENCES
                COUNTRIES (COUNTRYCODE) ENABLE ;

ORACLE.TICKETS_CLASSIFICATION:
CREATE TABLE TICKETS_CLASSIFICATION(CLASSIFICATIONCODE
                          VARCHAR2(5), PRIMARY KEY(CLASSIFICATIONCODE))
                          ;

MSSQL.ZONE_GROUP:
CREATE TABLE ZONE_GROUP(InterfaceCode VARCHAR(1) NOT NULL,
              GroupZoneCode VARCHAR(10) NOT NULL, PRIMARY
              KEY(InterfaceCode,GroupZoneCode));

MSSQL.TICKETS_CLASSIFICATION:
CREATE TABLE TICKETS_CLASSIFICATION(ClassificationCode
                          VARCHAR(5), PRIMARY KEY(ClassificationCode))
                          ;
